(defn get-albums-xs
  []
  (map (fn [[artist album-xs genre sub-genre-xs] year id]
         (cs/join \,
                  [id
                   (str year)
                   (cs/join \
                            album-xs)
                   genre
                   (str "\""
                        (cs/join \,
                                 sub-genre-xs)
                        "\"")]))
       (gen/sample-seq album-gen)
       (mapcat (fn [year]
                 (repeat (rand-nth (range 10 15))
                         year))
               (drop 2040 (range)))
       (drop 501 (range))))
